mapper.select:
SELECT PARTY_NO,PARTY_MASTER,USER_NICKNAME,USER_LEVEL,LEVEL_NAME,PARTY_COUNT,PARTY_OTTENG,OTT_KOR,PARTY_STARTDATE,PARTY_ENDDATE,PARTY_RULE FROM PARTY JOIN OTT_TYPE ON(PARTY_OTTENG=OTT_ENG) JOIN USERINFO ON(PARTY_MASTER=USER_ID) JOIN USERLEVEL ON(USER_LEVEL=LEVEL_CODE) WHERE PARTY_STATUS='Y'
AND OTT_ENG=#{findOtt}
ORDER BY PARTY_NO DESC

mapper.insert:
INSERT INTO PARTY (
				PARTY_NO
			   ,PARTY_MASTER
			   ,PARTY_COUNT
			   ,PARTY_OTTENG
			   ,PARTY_ENDDATE
			   ,PARTY_ACCOUNTBANK
			   ,PARTY_ACCOUNTNUM
			   ,PARTY_RULE
			   ,PARTY_OTTID
			   ,PARTY_OTTPWD
			   ) 
VALUES (
		 SEQ_PNO.NEXTVAL
		,#{partyMaster}
		,#{partyCount}
		,#{partyOttEng}
		,#{partyEndDate}
		,#{partyAccountBank}
		,#{partyAccountNum}
		,#{partyRule}
		,#{partyOttId}
		,#{partyOttPwd}
		)

INSERT INTO PAYMENT(
					 PAYMENT_PID
					,PAY_PARTY_NO
					,PAY_ID
					,PAY_NO
					)
VALUES(
		 SEQ_PMNO.NEXTVAL
		,#{payPartyNo}
		,#{payUserId}
		,#{payConfirmNo}
		)

INSERT INTO PARTY_MEMBER(
					 JOIN_NO
					,JOIN_PARTY
					,JOIN_MEMBER
					)
VALUES(
		 SEQ_JNO.NEXTVAL
		,#{joinParty}
		,#{joinMember}
		)

mapper.update:
UPDATE PARTY
SET PARTY_STATUS = 'N'
WHERE PARTY_NO = #{partyNo}
